// WICHTIG: Getroffene Entscheidungen werden aus verfügbaren Entscheidungen entfernt
export const getAvailableDecisions = (currentDate: any, gameState: any): Decision[] => {
  const takenDecisionIds = gameState.decisionHistory.map((result: any) => result.decisionId);
  
  return initialDecisions.filter(decision => {
    // Entscheidung wurde bereits getroffen - NICHT mehr verfügbar
    if (takenDecisionIds.includes(decision.id)) {
      return false;
    }
    
    // Alle anderen Entscheidungen sind IMMER verfügbar (keine Dependencies)
    return true;
  });
};

export const getDecisionById = (id: string): Decision | undefined => {
  return initialDecisions.find(decision => decision.id === id);
};

export const getDecisionsByCategory = (category: Decision['category']): Decision[] => {
  return initialDecisions.filter(decision => decision.category === category);
};

export default initialDecisions;
